function toggleWatchlist(i,t,c,a){const o={nom:t,affiche:c,emplacement:a},s=JSON.parse(localStorage.getItem("watchlist"))||[],l=s.findIndex(r=>r.nom===t);l===-1?(s.push(o),localStorage.setItem("watchlist",JSON.stringify(s)),i.innerHTML='<i class="fa fa-check"></i>Supprimer de ma liste',i.onclick=()=>toggleWatchlist(i,t,c,a)):(s.splice(l,1),localStorage.setItem("watchlist",JSON.stringify(s)),i.innerHTML='<i class="fa fa-plus"></i>Ajouter \xE0 ma liste',i.onclick=()=>toggleWatchlist(i,t,c,a))}
